clc
clear
close all

%  dataAI
load dataAI1.mat  % dataAI

ndata = length(label);     %

rng(15); %
nnum = fix(ndata*0.8); %
list = randperm(ndata,nnum);

totalData = data;
totalLabel = label;

data = totalData(:,:,list);
label = totalLabel(:,list);

csdata = totalData;
csdata(:,:,list)=[];

cslabel = totalLabel;
cslabel(:,list) = [];
ndata = nnum;

% 
% nnodel = [12; 3; 2 ];          %  for dataAI
nnodel = [36; 4; 3 ];          %  for dataAI1

nlay = length( nnodel );      % AI

for i=1 : nlay - 1
    tmplay = rand( nnodel( i ) + 1, nnodel( i +1 ))';
    lay(i).wb = tmplay ;
end

step = 0.2;     %  / 
iter = 1;
while 1
    totalc = 0;
    % dwb
    for n =1 : nlay - 1
        tmpdwb = zeros(nnodel( n +1 ), nnodel( n ) + 1);
        dwb(n).dwb = tmpdwb;
    end

    for i = 1 : ndata

        tmpa = data(:,:,i);     %   a

        %  
        tmp(1).z = [];
        tmp(1).a = tmpa(:);     %vec
        tmp(1).a1 = [];

        %    z  a  a' ()
        for n = 2 : nlay
            tmp(n).z = lay(n-1).wb(:,1:end-1) * tmp(n-1).a + lay(n-1).wb(:,end);
            tmp(n).a = 1./(1+exp(-tmp(n).z));
            tmp(n).a1 = tmp(n).a .* (1-tmp(n).a);
        end

        tdata(i).data = tmp;

        % 
        yc(i) =norm((tmp(n).a==max(tmp(n).a)) - label(:,i));

        % 
        tmpc =  tmp(n).a - label(:,i);
        tdata(i).C = tmpc'*tmpc/2;
        totalc =totalc+tdata(i).C; 

        % wb
        % step 1 delta 
        tmp(n).delta = tmpc.*tmp(n).a1;         % P149 eq(7)   
        for n = nlay-1 : -1 : 2
            tmp(n).delta = (lay(n).wb(:,1:end-1)'*tmp(n+1).delta) .* tmp(n).a1;  % P151 eq(16)
        end

        % step 2. wb P144 eq(11)
        for n = 1 : nlay - 1
            dwb(n).dwb =dwb(n).dwb + tmp(n+1).delta * [tmp(n).a ; 1]';
        end
    end

    % wb
    for n =1 : nlay - 1
        lay(n).wb = lay(n).wb - step * dwb(n).dwb ;
    end

    if (ndata-sum(~yc) == 0)&& iter>200
        break;
    end
    % 
    err(iter) = totalc;
    errrate(iter) = (ndata-sum(~yc))/ndata*100;
    iter = iter + 1;
end

yyaxis left
plot(err);ylabel('totalC')
hold on;
yyaxis right
plot(errrate);ylabel('errRate(%)')
